$(function () {
    $(document).foundation();

    $('.ui.dropdown').dropdown();
    $('.ui.checkbox').checkbox();
    $('.menu .item').tab();
    $('.show-hide-sidebar, .ui.popup-item').popup();
    $('.breadcrumb-nested').popup({
        inline: true,
        hoverable: true,
        position: 'bottom left'
    });

    $('textarea').autosize();

    resizeButtonsBlock();
    $(window).on('resize', resizeButtonsBlock);

    //$(document).on('click', "[data-toggle]", function () {
    //    $(this).next().toggle();
    //});

    var selector = '',
        types = ['jpg', 'jpeg', 'png', 'gif'];
    for (var i = 0; i < types.length; i++) {
        selector += "a[href$='." + types[i].toLowerCase() + "'],a[href$='." + types[i].toUpperCase() + "']";
        if (i + 1 != types.length) {
            selector += ",";
        }
    }
    var $linkWithImage = $(selector);
    $linkWithImage
        .attr('rel', 'fancybox')
        .fancybox({
            openEffect: 'elastic',
            closeEffect: 'elastic',
            helpers: {
                title: {
                    type: 'inside'
                },
                buttons: {}
            }
        });

    $.mtooltip('[rel~=tooltip]');

    if (!$.cookie('sidebar-show')) {
        resizeButtonsBlock();
    }
});

$(document).on('click', '.mmodal', function (e) {
    e.preventDefault();
    var $this = $(this);
    $this.mmodal({
        width: $this.data('width')
    });
    return false;
});

$(document).on('click', '#hide-sidebar', function (e) {
    e.preventDefault();
    var $sidebar = $('#sidebar-td');
    $sidebar.toggle();

    var isShow = $sidebar.css('display') != 'none';
    $.cookie('sidebar-show', isShow);
    var $content = $('#content-td');
    if (isShow) {
        $content.css('margin-left', 250);
    } else {
        $content.css('margin-left', 0);
    }
    resizeButtonsBlock();
    return false;
});

$(document).on('click', '.flash-list .remove', function (e) {
    e.preventDefault();
    var $list = $(e.target).closest('.flash-list');
    if ($list.children().length == 1) {
        $list.hide();
    } else {
        $(this).closest('li').hide();
    }
    return false;
});

function popupWindow(url, title, w, h) {
    var left = (screen.width / 2) - (w / 2),
        top = (screen.height / 2) - (h / 2);
    return window.open(url, title, 'toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=no, copyhistory=no, width=' + w + ', height=' + h + ', top=' + top + ', left=' + left);
}

$(document).on('click', '.window-open', function (e) {
    e.preventDefault();
    var $this = $(this);
    popupWindow($this.attr('href'), $this.attr('title'), ($this.data('width') || 650), ($this.data('height') || 650)).print();
    return false;
});

$(document).on('click', '[data-confirm]', function (e) {
    return confirm($(this).data('confirm'));
});
// using jQuery
function getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie != '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = jQuery.trim(cookies[i]);
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) == (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

var csrftoken = getCookie('X-CSRFToken');

function csrfSafeMethod(method) {
    // these HTTP methods do not require CSRF protection
    return (/^(GET|HEAD|OPTIONS|TRACE)$/.test(method));
}

$.ajaxSetup({
    crossDomain: false, // obviates need for sameOrigin test
    beforeSend: function (xhr, settings) {
        if (!csrfSafeMethod(settings.type)) {
            xhr.setRequestHeader("X-CSRFToken", csrftoken);
        }
    }
});


