$(document).on('click', '.toolbar .search', function (e) {
    e.preventDefault();
    $('.toolbar').addClass('search');
    $('.page-size').addClass('search');
    $('.toolbar .search-toolbar input').focus();
    return false;
});

$(document).on('click', '.toolbar .exit-search', function (e) {
    e.preventDefault();
    $('.toolbar').removeClass('search');
    $('.page-size').removeClass('search');
    $('.toolbar .search-toolbar input').val('');

    var searchVar = 'search';
    var $list = $('#list');
    var url = $list.data('path');

    url = url.replace(new RegExp("(&|\\?)" + searchVar + "=.*?(&|$)", 'g'), function (str, p1, p2, offset, s) {
        if (p1 == '?') {
            return '?';
        } else if (p2 == '') {
            return '';
        }
        return '&';
    });

    var data = {};
    data[searchVar] = '';
    $.ajax({
        url: url,
        data: data,
        success: function (html) {
            $list.replaceWith($(html).find('#list'));
        }
    });
    return false;
});

$(document).on('keydown', '.toolbar .search-toolbar input', function (e) {
    if (e.keyCode == 13) {
        e.preventDefault();
        return false;
    }
});

$(document).on('keyup', '.toolbar .search-toolbar input', function (e) {
    e.preventDefault();

    var searchVar = 'search',
        $this = $(this),
        updateTimer;

    if (e.keyCode == 27) {
        $('.toolbar').removeClass('search');
        $('.page-size').removeClass('search');
    } else {
        clearTimeout(updateTimer);
        updateTimer = setTimeout(function () {
            var $list = $('#list');
            var url = $list.data('path');

            url = url.replace(new RegExp("(&|\\?)" + searchVar + "=.*?(&|$)", 'g'), function (str, p1, p2, offset, s) {
                if (p1 == '?') {
                    return '?';
                } else if (p2 == '') {
                    return '';
                }
                return '&';
            });

            var data = {};
            data[searchVar] = $this.val();
            $.ajax({
                url: url,
                data: data,
                success: function (html) {
                    $list.replaceWith($(html).find('#list'));
                }
            });
        }, 300);
    }

    return false;
});

/**
 * Example:
 * $(window).resize(function (e) {
 *      delay(function() { ... }, 300);
 * });
 */
var delay = (function(){
    var timer = 0;
    return function(callback, ms){
        clearTimeout (timer);
        timer = setTimeout(callback, ms);
    };
})();
